INSERT INTO `drug` (`ID`,`DRUG_NAME`,`DOSAGE_ADMINISTRATION`,`DOSAGE_FORM_STRENGTH`,`CONTRAINDICTIONS`,`WARNINGS_PRECAUSTIONS`,`ADVERSE_REACTIONS`,`DRUG_INTERACTIONS`,`USE_IN_SPECIFIC_POPULATIONS`,`AVAILABLE_AT_AVELLA`,`DRUG_CATEGORY_ID`,`SITE_URL`,`INDICATIONS_USAGE`,`DRUG_TYPE`,`PRESCRIBING_INFO`) VALUES (26,'ACTIQ','<ul>	<li>Patients must require and use around-the-clock opioids when taking ACTIQ.</li>	<li>Use the lowest effective dosage for the shortest duration consistent with individual patient treatment goals.</li>	<li>Individualize dosing based on the severity of pain, patient response, prior analgesic experience, and risk factors for addiction, abuse, and misuse.</li>	<li>Initial dose of ACTIQ: 200 mcg. Prescribe an initial supply of six 200 mcg ACTIQ units.</li>	<li>Individually titrate to a tolerable dose that provides adequate analgesia using single ACTIQ dosage unit per breakthrough cancer pain episode.</li>	<li>No more than two doses can be taken per breakthrough pain episode.</li>	<li>Wait at least 4 hours before treating another episode of breakthrough pain with ACTIQ.</li>	<li>Limit consumption to four or fewer units per day once successful dose is found.</li>	<li>When opioid therapy is no longer required, consider discontinuing ACTIQ along with a gradual downward of other opioids to minimize possible withdrawal effects.</li></ul>','Solid oral transmucosal lozenge: 200 mcg, 400 mcg, 600 mcg, 800 mcg, 1200 mcg,and 1600 mcg','<ul>	<li>Opioid non-tolerant patients.</li>	<li>Significant respiratory depression.</li>	<li>Management of acute or postoperative pain including headache/migraines and dental pain.</li>	<li>Acute or severe bronchial asthma in an unmonitored setting or in absence of resuscitative equipment.</li>	<li>Known or suspected gastrointestinal obstruction, including paralytic ileus.</li>	<li>Known hypersensitivity to fentanyl or components of ACTIQ.</li></ul>','<ul>	<li>Life-Threatening Respiratory Depression in Patients with Chronic Pulmonary Disease or in Elderly, Cachectic, or Debilitated Patients: Monitor closely, particularly during initiation and titration.</li>	<li>Serotonin Syndrome: Potentially life-threatening condition could result from concomitant serotonergic drug administration. Discontinue ACTIQ if serotonin syndrome is suspected.</li>	<li>Adrenal Insufficiency: If diagnosed, treat with physiologic replacement of corticosteroids, and wean patient off of the opioid.</li>	<li>Severe Hypotension: Monitor during dosage initiation and titration. Avoid use of ACTIQ in patients with circulatory shock.</li>	<li>Risks of Use in Patients with Increased Intracranial Pressure, Brain Tumors, Head Injury, or Impaired Consciousness: Monitor for sedation and respiratory depression. Avoid use of ACTIQ in patients with impaired consciousness or coma.</li></ul>','Most common (frequency ?5%): nausea, dizziness, somnolence, vomiting, asthenia,and headache, dyspnea, constipation, anxiety, confusion, depression, rash, andinsomnia.','Mixed Agonist/Antagonist and Partial Agonist Opioid Analgesics: Avoid the use of mixed agonist/antagonist or partial agonist analgesics in patients who are already receiving a full opioid agonist analgesic (including ACTIQ) because they may reduce analgesic effect of ACTIQ or precipitate withdrawal symptoms.','<ul>	<li>Pregnancy: May cause fetal harm.</li>	<li>Lactation: Not recommended.</li>	<li>Renal and Hepatic Impairment: Administer ACTIQ with caution.</li></ul>',1,4,'http://www.actiq.com/','ACTIQ is an opioid agonist indicated for the management of breakthrough pain in cancer patients 16 years of age and older who are already receiving and who are tolerant to around-the-clock opioid therapy for their underlying persistent cancer pain. Patients considered opioid tolerant are those who are taking, for one week or longer, around-the-clock medicine consisting of at least 60 mg of oral morphine per day, at least 25 mcg of transdermal fentanyl per hour, at least 30 mg of oral oxycodone per day, at least 8 mg of oral hydromorphone per day, at least 25 mg oral oxymorphone per day, at least 60 mg oral hydrocodone per day, or an equianalgesic dose of another opioid daily for a week or longer. Patients must remain on around-the-clock opioids while taking ACTIQ.<br>Limitations of Use:<br><ul>	<li>Not for use in opioid non-tolerant patients.</li><li>Not for use in the management of acute or postoperative pain, including headache/ migraine or dental pain. </li><li>As a part of the TIRF REMS Access program, ACTIQ may be dispensed only to outpatients enrolled in the program.  For inpatient administration of ACTIQ (e.g., hospitals, hospices, and long-term care facilities that prescribe for inpatient use), patient and prescriber enrollment is not required.</li></ul>',1,'http://www.actiq.com/pdf/ActiqDigitalPIandMedGuide.pdf');
INSERT INTO `drug` (`ID`,`DRUG_NAME`,`DOSAGE_ADMINISTRATION`,`DOSAGE_FORM_STRENGTH`,`CONTRAINDICTIONS`,`WARNINGS_PRECAUSTIONS`,`ADVERSE_REACTIONS`,`DRUG_INTERACTIONS`,`USE_IN_SPECIFIC_POPULATIONS`,`AVAILABLE_AT_AVELLA`,`DRUG_CATEGORY_ID`,`SITE_URL`,`INDICATIONS_USAGE`,`DRUG_TYPE`,`PRESCRIBING_INFO`) VALUES (27,'ABSTRAL','<ul><li>Patients must require and use around-the-clock opioids when taking ABSTRAL</li>	<li>Use the lowest effective dosage for the shortest duration consistent with individual patient treatment goals.</li>	<li>Individualize dosing based on the severity of pain, patient response, prior analgesic experience, and risk factors for addiction, abuse, and misuse.</li>	<li>Administer on the floor of the mouth directly under the tongue and allow to completely dissolve.</li>	<li>Initial dose of ABSTRAL: 100 mcg.</li>	<li>No more than two doses can be taken per breakthrough pain episode.</li>	<li>Individually titrate to a tolerable dose that provides adequate analgesia.</li><li>Wait at least 2 hours before treating another episode of breakthrough pain with ABSTRAL.</li><li>Limit consumption to treat four or fewer breakthrough pain episodes per day once a successful dose is found.</li>	<li>When opioid therapy is no longer required, consider discontinuing ABSTRAL along with a gradual downward of other opioids to minimize possible withdrawal effects.</li></ul>','Sublingual tablets: 100 mcg, 200 mcg, 300 mcg, 400 mcg, 600 mcg and 800mcg strengths as fentanyl base.','<ul>	<li>Opioid non-tolerant patients.</li><li>Management of acute or postoperative pain including headache/migraines dental pain, or use in the emergency department.</li>	<li>Acute or severe bronchial asthma in an unmonitored setting or in absence of resuscitative equipment.</li>	<li>Known or suspected gastrointestinal obstruction, including paralytic ileus.</li><li> Known hypersensitivity to fentanyl or components of ABSTRAL</li></ul>','<ul><li>Life-Threatening Respiratory Depression in Patients with Chronic Pulmonary Disease or in Elderly, Cachectic, or Debilitated Patients: Monitor closely, particularly during initiation and titration.  Serotonin Syndrome: Potentially life-threatening condition could result from concomitant serotonergic drug administration. Discontinue ABSTRAL if serotonin syndrome is suspected.</li><li>Adrenal Insufficiency: If diagnosed, treat with physiologic replacement of corticosteroids, and wean patient off of the opioid.</li><li>Severe Hypotension: Monitor during dosage initiation and titration. Avoid use of ABSTRAL in patients with circulatory shock.</li><li>Risks of Use in Patients with Increased Intracranial Pressure, Brain Tumors, Head Injury, or Impaired Consciousness: Monitor for sedation and respiratory depression. Avoid use of ABSTRAL in patients with impaired consciousness or coma.</li></ul>','Most common (total frequency ?3%): nausea, somnolence, headache, andconstipation.','Mixed Agonist/Antagonist and Partial Agonist Opioid Analgesics: Avoid use with ABSTRAL because they may reduce analgesic effect of ABSTRAL or precipitate withdrawal symptoms','<ul>	<li>Pregnancy: May cause fetal harm.</li>	<li>Lactation: Not Recommended.</li><li>Renal and Hepatic Impairment: Administer ABSTRAL with caution.</li></ul>',1,4,'http://www.sentynl.com/abstral','ABSTRAL is an opioid agonist indicated for the management of breakthrough pain in cancer patients 18 years of age and older who are already receiving and who are tolerant to around-the-clock opioid therapy for their underlying persistent cancer pain.Patients considered opioid tolerant are those who are taking, for one week or longer, around-the-clock medicine consisting of at least 60 mg of oral morphine per day, at least 25 mcg per hour of transdermal fentanyl, at least 30 mg of oral oxycodone per day, at least 8 mg of oral hydromorphone per day, at least 25 mg oral oxymorphone per day, at least 60 mg oral hydrocodone per day, or an equianalgesic dose of another opioid daily for a week or longer. Patients must remain on around-the-clock opioids while taking ABSTRAL.Limitations of Use:<ul><li>Not for use in opioid non-tolerant patients.</li><li>Not for use in the management of acute or postoperative pain, including headache/migraine, dental pain, or in the emergency department.</li><li>As a part of the TIRF REMS Access program, ABSTRAL may be dispensed only to outpatients enrolled in the program. For inpatient administration of ABSTRAL (e.g., hospitals, hospices, and long-term care facilities that prescribe for inpatient use), patient and prescriber enrollment is not required.</li></ul>',1,'https://static1.squarespace.com/static/564f5e8be4b0598988787d65/t/5862e97ef7e0abc3cf8c38b5/1482877328153/Abstral_Approved_Dec+2016.pdf');
